div.script:
/**/
				$(function () {
				$('a[data-toggle="tab"]').on('show.bs.tab', function (e) {
				$.cookie('activeTab', $(e.target).attr('aria-controls'));
				});
				$('.standard_link_action').on('click', function (e) {
				e.preventDefault();
				var th = this;
				$.getJSON($(this).attr('href'), {}, function (data) {
				if (data.success) {
				document.location.reload();
				}
				else {
				alert(data.notices);
				}
				});
				});
				});

